import React, { useState, useEffect } from "react";
import { PageArea, Fake } from "./styled";
import { useParams } from "react-router-dom";
import useApi from "../../helpers/OlxApi";
import { PageContainer } from "../../components/MainComponents";
import { Slide } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";

const App = () => {
  function formatDate(date) {
    let cDate = new Date(date);
    let months = [
      "janeiro",
      "fevereiro",
      "março",
      "maio",
      "abril",
      "junho",
      "julho",
      "agosto",
      "setembro",
      "outro",
      "novembro",
      "dezembro",
    ];
    let cDay = cDate.getDate();
    let cMonth = cDate.getMonth();
    let cYear = cDate.getFullYear();

    return `${cDay} de ${months[cMonth]} de ${cYear}`;
  }
  const api = useApi();
  const { id } = useParams();

  const [loading, setLoading] = useState(true);
  const [adInfo, setAdInfo] = useState({});

  useEffect(() => {
    const getAdInfo = async (id) => {
      const json = await api.getAd(id, true);
      console.log(json);
      setAdInfo(json);
      setLoading(false);
    };
    getAdInfo(id);
  }, []);
  return (
    <PageContainer>
      <PageArea>
        <div className="leftSide">
          <div className="box">
            <div className="adImage">
              {loading && <Fake height={300} />}
              {adInfo.images && (
                <Slide>
                  {adInfo.images.map((img, k) => (
                    <div key={k} className="each-slide">
                      <img src={img} alt="" />
                    </div>
                  ))}
                </Slide>
              )}
            </div>
            {adInfo.title && <h2>{adInfo.title}</h2>}
            {adInfo.dateCreated && (
              <small>Criado em {formatDate(adInfo.dateCreated)}</small>
            )}

            <div className="adInf">
              <div className="adName">{loading && <Fake height={20} />}</div>
              <div className="adDescription">
                {loading && <Fake height={100} />}
                {adInfo.description}
                <hr />
                {adInfo.views && <small>Vizualiações: {adInfo.views}</small>}
              </div>
            </div>
          </div>
        </div>
        <div className="rightSide">
          <div className="box box--pading">
            {" "}
            {loading && <Fake height={20} />}
            {adInfo.priceNegoatiable && "preço negociável"}
            {!adInfo.priceNegoatiable && adInfo.price && (
              <div className="price">
                "Preço: <span>R$ ${adInfo.price}</span>"
              </div>
            )}
          </div>
          <div className="box box--pading">
            {" "}
            {loading && <Fake height={50} />}
          </div>
        </div>
      </PageArea>
    </PageContainer>
  );
};

export default App;
