    border-radius: 5px;
    box-shadow: 0px 0px 4px #999;
    margin-bottom: 20px;
  }

  .leftSide {
    flex: 1;
    margin-right: 20px;
    .adImage {
    }
    .adInfo {
      .adName {
        margint-top: 20px;
      }

      .adDescription {
      }
    }
  }

  .rightSide {
    width: 250px;
  }
`;
