import styled from "styled-components";

export const SearchArea = styled.div`
  background-color: #ddd;
  boder-bottom: #ccc;
  padding: 20px 0;
  .searchBox {
    background-color: #9bb83c;
    padding: 20px 15px;
    border-radius: 5px;
    box-shadow: 1px 1px 1px 0.3px rgba(0, 0, 0, 0.2);
    display: flex;

    form {
      flex: 1;
      display: flex;

      input,
      select {
        heigt: 40px;
        border: 0;
        border-radiux: 5px;
        outline: 0;
        font-size: 15px;
        color: black;
        margin-right: 20px;
      }
    }
  }
`;
export const PageArea = styled.div``;
