import React, { useState, useEffect } from "react";
import { PageArea, Fake, OthersArea, BreadChumb } from "./styled";
import { useParams, Link } from "react-router-dom";
import useApi from "../../helpers/OlxApi";
import { PageContainer } from "../../components/MainComponents";
import { Slide } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";
import AdItem from "../../components/partials/AdItem";

const App = () => {
  function formatDate(date) {
    let cDate = new Date(date);
    let months = [
      "janeiro",
      "fevereiro",
      "março",
      "maio",
      "abril",
      "junho",
      "julho",
      "agosto",
      "setembro",
      "outro",
      "novembro",
      "dezembro",
    ];
    let cDay = cDate.getDate();
    let cMonth = cDate.getMonth();
    let cYear = cDate.getFullYear();

    return `${cDay} de ${months[cMonth]} de ${cYear}`;
  }
  const api = useApi();
  const { id } = useParams();

  const [loading, setLoading] = useState(true);
  const [adInfo, setAdInfo] = useState({});

  useEffect(() => {
    const getAdInfo = async (id) => {
      const json = await api.getAd(id, true);
      console.log(json);
      setAdInfo(json);
      setLoading(false);
    };
    getAdInfo(id);
  }, []);
  return (
    <PageContainer>
      {adInfo.category && (
        <BreadChumb>
          Você está aqui:
          <Link to="/">Home</Link>/<Link to="">{adInfo.stateName}</Link>/
          {<Link to="">{adInfo.category.name}</Link>}/{adInfo.title}
        </BreadChumb>
      )}
      <PageArea>
        <div className="leftSide">
          <div className="box">
            <div className="adImage">
              {loading && <Fake height={300} />}
              {adInfo.images && (
                <Slide>
                  {adInfo.images.map((img, k) => (
                    <div key={k} className="each-slide">
                      <img src={img} alt="" />
                    </div>
                  ))}
                </Slide>
              )}
            </div>
            {adInfo.title && <h2>{adInfo.title}</h2>}
            {adInfo.dateCreated && (
              <small>Criado em {formatDate(adInfo.dateCreated)}</small>
            )}

            <div className="adInf">
              <div className="adName">{loading && <Fake height={20} />}</div>
              <div className="adDescription">
                {loading && <Fake height={100} />}
                {adInfo.description}
                <hr />
                {adInfo.views && <small>Vizualiações: {adInfo.views}</small>}
              </div>
            </div>
          </div>
        </div>
        <div className="rightSide">
          <div className="box box--pading">
            {" "}
            {loading && <Fake height={20} />}
            {adInfo.priceNegoatiable && "preço negociável"}
            {!adInfo.priceNegoatiable && adInfo.price && (
              <div className="price">
                Preço: <span>R$ {adInfo.price}</span>
              </div>
            )}
          </div>
          {loading && <Fake height={50} />}
          {adInfo.userInfo && (
            <>
              <a
                href={`mailto:${adInfo.userInfo.email}`}
                target="_blank"
                className="contactSellerLink"
              >
                Fale com o vendedor
              </a>
              <div className="box box--pading createdBy">
                <strong>{adInfo.userInfo.name}</strong>
                <small>E-mail: {adInfo.userInfo.email}</small>
                <small>E-mail: {adInfo.stateName}</small>
              </div>
            </>
          )}
        </div>
      </PageArea>
      <OthersArea>
        {adInfo.others && (
          <>
            <h2>Outras ofertas do vendedor</h2>
            <div className="list">
              {adInfo.others.map((i, k) => (
                <AdItem key={k} data={i} />
              ))}
            </div>
          </>
        )}
      </OthersArea>
    </PageContainer>
  );
};

export default App;
