import styled from "styled-components";

export const Fake = styled.div`
  background-color: #ddd;
  height: ${(props) => props.height || 20}px;
`;
export const PageArea = styled.div`
  display: flex;
  margin-top: 20px;

  .box {
    display: flex;
    background-color: #fff;
    border-radius: 5px;
    box-shadow: 0px 0px 4px #999;
    margin-bottom: 20px;
  }
  .box--pading {
    padding: 10px;
  }

  .leftSide {
    flex: 1;
    margin-right: 20px;

    .adImage {
      width: 320px;
      height: 320px;
      margin-right: 20px;

      .each-slide img {
        display: flex;
        align-items: center;
        justify-content: center;
        background-size: cover;
        height: 320px;
      }
    }
    .adInf {
      padding: 10px;
      .adName {
        margin-bottom: 20px;
      }

      .adDescription {
      }
    }
  }

  .rightSide {
    width: 250px;
  }
`;
